import React from "react";

const PromptInput = ({ prompt, setPrompt }) => (
  <div className="p-3">
    <textarea
      className="w-full border p-2"
      placeholder="Enter custom instruction..."
      value={prompt}
      onChange={(e) => setPrompt(e.target.value)}
    />
  </div>
);

export default PromptInput;
