import React, { useState } from "react";
import { sendEmail } from "../api/api";

const ShareSummary = ({ summary }) => {
  const [emails, setEmails] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (!emails.trim()) {
      alert("‚ö†Ô∏è Please enter at least one email.");
      return;
    }
    setLoading(true);
    try {
      const recipients = emails.split(",").map((e) => e.trim());
      await sendEmail(summary, recipients);
      alert("‚úÖ Summary sent successfully!");
      setEmails("");
    } catch (err) {
      alert("‚ùå Failed to send. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 max-w-2xl mx-auto bg-white rounded-2xl shadow-md border border-gray-100">
      <h2 className="text-xl font-semibold text-gray-800 mb-4">
        Share Summary via Email
      </h2>

      <input
        type="text"
        placeholder="Enter recipient emails, comma separated"
        className="w-full p-3 border border-gray-300 rounded-xl shadow-sm focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none transition-all text-gray-700 placeholder-gray-400"
        value={emails}
        onChange={(e) => setEmails(e.target.value)}
      />

      <button
        className={`mt-4 w-full py-3 px-4 rounded-xl text-white font-medium transition-all shadow-md ${
          loading
            ? "bg-gray-400 cursor-not-allowed"
            : "bg-indigo-600 hover:bg-indigo-700"
        }`}
        onClick={handleSend}
        disabled={loading}
      >
        {loading ? "Sending..." : "üì© Share via Email"}
      </button>

      <p className="text-sm text-gray-500 mt-3">
        Tip: You can add multiple emails separated by commas.
      </p>
    </div>
  );
};

export default ShareSummary;
