import React from "react";

const PromptInput = ({ prompt, setPrompt }) => (
  <div className="p-6 max-w-2xl mx-auto bg-white rounded-2xl shadow-md border border-gray-100">
    <label className="block text-lg font-semibold text-gray-800 mb-2">
      Custom Instruction
    </label>
    <textarea
      className="w-full h-32 p-4 border border-gray-300 rounded-xl shadow-sm focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none transition-all text-gray-700 placeholder-gray-400 resize-none"
      placeholder="Enter custom instruction..."
      value={prompt}
      onChange={(e) => setPrompt(e.target.value)}
    />
    <p className="text-sm text-gray-500 mt-2">
      Provide any specific instructions or context for generating better summaries.
    </p>
  </div>
);

export default PromptInput;
