import React from "react";

const SummaryEditor = ({ summary, setSummary }) => (
  <div className="p-3">
    <textarea
      className="w-full h-40 border p-2"
      value={summary}
      onChange={(e) => setSummary(e.target.value)}
    />
  </div>
);

export default SummaryEditor;
