import React from "react";

const SummaryEditor = ({ summary, setSummary }) => (
  <div className="p-6 max-w-3xl mx-auto">
    <label className="block text-lg font-semibold text-gray-800 mb-2">
      Edit Meeting Summary
    </label>
    <textarea
      className="w-full h-48 p-4 border border-gray-300 rounded-2xl shadow-sm focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none resize-none text-gray-700 placeholder-gray-400 transition-all"
      placeholder="Write or edit the meeting summary here..."
      value={summary}
      onChange={(e) => setSummary(e.target.value)}
    />
    <p className="text-sm text-gray-500 mt-2">
      Make sure your edits are clear and concise.
    </p>
  </div>
);

export default SummaryEditor;
