import React from "react";

const FileUpload = ({ setTranscript }) => {
  const handleFile = (e) => {
    const reader = new FileReader();
    reader.onload = (event) => setTranscript(event.target.result);
    reader.readAsText(e.target.files[0]);
  };

  return (
    <div className="p-6 max-w-2xl mx-auto bg-white rounded-2xl shadow-md border border-gray-100">
      <label className="block text-lg font-semibold text-gray-800 mb-4">
        Upload Transcript
      </label>

      <div className="flex flex-col items-center justify-center border-2 border-dashed border-gray-300 rounded-xl p-8 cursor-pointer hover:border-indigo-500 transition">
        <input
          type="file"
          accept=".txt"
          onChange={handleFile}
          className="hidden"
          id="fileInput"
        />
        <label
          htmlFor="fileInput"
          className="flex flex-col items-center space-y-2 cursor-pointer"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-12 w-12 text-indigo-500"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M7 16V4m0 0l-4 4m4-4l4 4m6 0h2a2 2 0 012 2v12a2 2 0 01-2 2H5a2 2 0 01-2-2V10a2 2 0 012-2h2"
            />
          </svg>
          <span className="text-gray-600 font-medium">
            Drag & Drop or <span className="text-indigo-600">Browse</span> file
          </span>
          <span className="text-sm text-gray-400">Supports only .txt files</span>
        </label>
      </div>
    </div>
  );
};

export default FileUpload;
