import React, { useState } from "react";
import FileUpload from "../components/FileUpload";
import PromptInput from "../components/PromptInput";
import SummaryEditor from "../components/SummaryEditor";
import ShareSummary from "../components/ShareSummary";
import api from "../api/api";

const Home = () => {
  const [transcript, setTranscript] = useState("");
  const [prompt, setPrompt] = useState("");
  const [summary, setSummary] = useState("");

  const handleSummarize = async () => {
    try {
      const { data } = await api.post("/summarize", { transcript, prompt });
      setSummary(data.summary);
    } catch (error) {
      alert("Error generating summary");
    }
  };

  return (
    <div className="max-w-4xl mx-auto px-6 py-8">
      <h2 className="text-2xl font-bold mb-6">Meeting Summarizer</h2>

      <FileUpload setTranscript={setTranscript} />
      <PromptInput prompt={prompt} setPrompt={setPrompt} />

      <button
        onClick={handleSummarize}
        className="bg-green-600 text-white px-6 py-2 mt-4 rounded-lg shadow hover:bg-green-700 transition"
      >
        Generate Summary
      </button>

      {summary && (
        <>
          <SummaryEditor summary={summary} setSummary={setSummary} />
          <ShareSummary summary={summary} />
        </>
      )}
    </div>
  );
};

export default Home;
