import axios from "axios";

const API_BASE = "http://localhost:5000/api";

export const generateSummary = async (transcript, prompt) => {
  const res = await axios.post(`${API_BASE}/summarize`, { transcript, prompt });
  return res.data.summary;
};

export const sendEmail = async (summary, recipients) => {
  const res = await axios.post(`${API_BASE}/send-email`, { summary, recipients });
  return res.data;
};
